import { initializeApp, getApps } from 'firebase/app';
import { getDatabase } from 'firebase/database';

// Firebase configuration for sensor data database (ultrasonic-bot project)
const sensorDbConfig = {
  databaseURL: 'https://ultrasonic-bot-default-rtdb.asia-southeast1.firebasedatabase.app'
};

// Firebase configuration for human detection state database (taarini-testing project)
// If this doesn't work, check your Firebase console for the exact database URL
// It might be: https://taarini-testing-default-rtdb.firebaseio.com
// or: https://taarini-testing-default-rtdb.[region].firebasedatabase.app
const detectionDbConfig = {
  databaseURL: 'https://taarini-testing-default-rtdb.firebaseio.com'
};

// Initialize Firebase apps with unique names to allow multiple database connections
let sensorApp, detectionApp;

try {
  const existingSensorApp = getApps().find(app => app.name === 'sensorApp');
  sensorApp = existingSensorApp || initializeApp(sensorDbConfig, 'sensorApp');
} catch (error) {
  console.error('Error initializing sensor app:', error);
  sensorApp = initializeApp(sensorDbConfig, 'sensorApp');
}

try {
  const existingDetectionApp = getApps().find(app => app.name === 'detectionApp');
  detectionApp = existingDetectionApp || initializeApp(detectionDbConfig, 'detectionApp');
} catch (error) {
  console.error('Error initializing detection app:', error);
  detectionApp = initializeApp(detectionDbConfig, 'detectionApp');
}

// Get database instances
export const sensorDatabase = getDatabase(sensorApp);
export const detectionDatabase = getDatabase(detectionApp);

// Database paths
export const SENSOR_DATA_PATH = 'UltrasonicBot/Data';
export const DETECTION_STATE_PATH = 'remote/state';
