import { initializeApp } from 'firebase/app';
import { getDatabase } from 'firebase/database';

// Firebase configuration for sensor data database
const sensorDbConfig = {
  databaseURL: 'https://ultrasonic-bot-default-rtdb.asia-southeast1.firebasedatabase.app',
  // For public databases, we can use minimal config
  projectId: 'ultrasonic-bot'
};

// Firebase configuration for human detection state database
const detectionDbConfig = {
  databaseURL: 'https://taarini-testing-default-rtdb.firebaseio.com',
  projectId: 'taarini-testing'
};

// Initialize Firebase apps
// Using unique names to allow multiple database connections
let sensorApp, detectionApp;

try {
  sensorApp = initializeApp(sensorDbConfig, 'sensorApp');
} catch (error) {
  // If app already exists, get it
  const { getApps } = require('firebase/app');
  const apps = getApps();
  sensorApp = apps.find(app => app.name === 'sensorApp') || initializeApp(sensorDbConfig, 'sensorApp');
}

try {
  detectionApp = initializeApp(detectionDbConfig, 'detectionApp');
} catch (error) {
  // If app already exists, get it
  const { getApps } = require('firebase/app');
  const apps = getApps();
  detectionApp = apps.find(app => app.name === 'detectionApp') || initializeApp(detectionDbConfig, 'detectionApp');
}

// Get database instances
export const sensorDatabase = getDatabase(sensorApp);
export const detectionDatabase = getDatabase(detectionApp);

// Database paths
export const SENSOR_DATA_PATH = 'UltrasonicBot/Data';
export const DETECTION_STATE_PATH = 'remote/state';
